DROP TABLE IF EXISTS PublicRecipe, UserRecipe, UserPhoto, User, UserQuestionnaire, UserWorkout, PublicWorkout;

CREATE TABLE User
(
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    username VARCHAR(32) NOT NULL,
    password VARCHAR(256) NOT NULL,          /* To store Salted and Hashed Password Parts */
    first_name VARCHAR(32) NOT NULL,
    middle_name VARCHAR(32),
    last_name VARCHAR(32) NOT NULL,
    address1 VARCHAR(128) NOT NULL,
    address2 VARCHAR(128),
    city VARCHAR(64) NOT NULL,
    state VARCHAR(2) NOT NULL,
    zipcode VARCHAR(10) NOT NULL,            /* e.g., 24060-1804 */
    security_question_number INT NOT NULL,   /* Refers to the number of the selected security question */
    security_answer VARCHAR(128) NOT NULL,
    email VARCHAR(128) NOT NULL,
    two_factor VARCHAR(8) NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE UserPhoto
(
    id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
    extension ENUM('jpeg', 'jpg', 'png', 'gif') NOT NULL,
    user_id INT UNSIGNED,
    FOREIGN KEY (user_id) REFERENCES User(id) ON DELETE CASCADE
);

CREATE TABLE UserRecipe
(
    id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
    user_id INT UNSIGNED,
    name VARCHAR(256) NOT NULL,
	category VARCHAR(128) NOT NULL,
	cuisine VARCHAR(128) NOT NULL,
	website_url VARCHAR(256) NOT NULL,
	photo_url VARCHAR(256) NOT NULL,
	publisher_name VARCHAR(256) NOT NULL,
	publisher_url VARCHAR(256) NOT NULL,
	ingredients VARCHAR(768) NOT NULL,
	nutrients VARCHAR(512) NOT NULL,
    dietLabels VARCHAR(768) NOT NULL,
    healthLabels VARCHAR(768) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES User(id) ON DELETE CASCADE
);

/* The Recipe table contains attributes of interest of a recipe. */
CREATE TABLE PublicRecipe
(
 	id INT UNSIGNED NOT NULL AUTO_INCREMENT,
	name VARCHAR(256) NOT NULL,
	category VARCHAR(128) NOT NULL,
	cuisine VARCHAR(128) NOT NULL,
	website_url VARCHAR(256) NOT NULL,
	photo_url VARCHAR(256) NOT NULL,
	publisher_name VARCHAR(256) NOT NULL,
	publisher_url VARCHAR(256) NOT NULL,
	ingredients VARCHAR(768) NOT NULL,
	nutrients VARCHAR(512) NOT NULL,
	PRIMARY KEY (id)
);


/* The UserQuestionnaire table contains the user's survey data. */
CREATE TABLE UserQuestionnaire
(
    id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
    date_entered DATE NOT NULL,    /* YYYY-MM-DD */
    questionnaire MEDIUMTEXT NOT NULL,    /* Max 16,777,215 characters for storing survey in JSON */
    user_id INT UNSIGNED,
    FOREIGN KEY (user_id) REFERENCES User(id) ON DELETE CASCADE
);

CREATE TABLE UserWorkout
(
    id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
    name VARCHAR(256) NOT NULL,
    category VARCHAR(100),
    muscle VARCHAR(256),
    equipment VARCHAR(256),
    image_url VARCHAR(256),
    description VARCHAR(2000),
    user_id INT UNSIGNED,
    FOREIGN KEY (user_id) REFERENCES User(id) ON DELETE CASCADE
);

CREATE TABLE PublicWorkout
(
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    name VARCHAR(256) NOT NULL,
    category VARCHAR(100),
    muscle VARCHAR(256),
    equipment VARCHAR(256),
    image_url VARCHAR(256),
    description VARCHAR(2000),
    PRIMARY KEY (id)
);

